import React, { Component } from 'react';
import { AuthUserContext, withAuthorization } from '../Session';
import { withFirebase } from '../Firebase';
import { Container, Row, Col, Form, Button} from 'react-bootstrap';
import * as ROUTES from '../../constants/routes';
import { Link, withRouter } from 'react-router-dom';
import { compose } from 'recompose';

const INITIAL_STATE = {
    name: '',
    description: '',
}

class AddPlayPage extends Component {
    constructor(props) {
        super(props);

        this.state = { ...INITIAL_STATE };
    }

    onSubmit = event => {
        this.props.firebase.db.collection("shows").add({
            name: this.state.name,
            description : this.state.description,
            created: this.props.firebase.firestore.FieldValue.serverTimestamp(),
        })
        .then(() => {
            this.setState({ ...INITIAL_STATE });
        })
        .then(() => {
            this.props.history.push(ROUTES.ADMIN);
        });
        event.preventDefault();
    };

    onChange = event => {
        this.setState({ [event.target.name]: event.target.value });
    };


    render() {
        const {
            name,
            description,
        } = this.state;
        const isInvalid = 
            name === '';
        return (
            <Container>
                <Row>
                    <Col>
                        <Form
                            onSubmit={this.onSubmit}
                        >
                            <Form.Group controlId="playName">
                                <Form.Label>Name</Form.Label>
                                <Form.Control name="name" onChange={this.onChange} type="text"></Form.Control>
                            </Form.Group>
                            <Form.Group controlId="playDescription">
                                <Form.Label>Description</Form.Label>
                                <Form.Control name="description" onChange={this.onChange} as="textarea" rows="3"></Form.Control>
                            </Form.Group>
                            <Button disabled={isInvalid} variant="light" type="submit">
                                Submit
                            </Button>
                        </Form>
                    </Col>
                </Row>
            </Container>
        );
    }
}

const condition = authUser => !!authUser;

export default withFirebase(withRouter(withAuthorization(condition)(AddPlayPage)));
