import React, { Component } from 'react';

import { AuthUserContext, withAuthorization } from '../Session';
import { withFirebase } from '../Firebase';
import { Container, Row, Col } from 'react-bootstrap';
import { Card } from 'semantic-ui-react';

class PlaysPage extends Component {
  constructor(props) {
    super(props);

    this.state = {
        loading: false,
        users: [],
    };
  }

  componentDidMount() {
    this.setState({ loading: true });


    this.unsubscribe = this.props.firebase.db.collection("users").where("creator", "==", withAuthorization.authUser).onSnapshot(snapshot => {
        const users = [];
        snapshot.forEach((doc) => {
            const { email, username } = doc.data();
            users.push({
                key: doc.id,
                doc,
                email,
                username,
            });
        });
        this.setState({
            users,
            loading: false,
        });
    });
  }

  componentWillUnmount() {
      this.unsubscribe && this.unsubscribe();
  }


  render() {
    const { users, loading } = this.state;

    return (
        <Row>
          <Col>
            <h1>Admin</h1>
            {loading && <div>Loading ...</div>}
            <UserList users={users} />
        </Col>
      </Row>
    );
  }
}

const UserList = ({ users }) => (
    <div className="d-flex flex-wrap justify-content-start">
        {users.map(user => (
          <div key={user.key} className="p-2">
            <Card>
              <Card.Content header="User">fdsfds</Card.Content>
              <Card.Content description={user.username}></Card.Content>
              <Card.Content description={user.email}></Card.Content>
            </Card>
          </div>
        ))}
    </div>
  );


const condition = authUser => !!authUser;

export default withAuthorization(condition)(PlaysPage);
