import React, { Component } from 'react';

import { Link } from 'react-router-dom';
import { withAuthUser, withAuthorization, withAuthentication } from '../Session';
import { withFirebase } from '../Firebase';
import { Container, Row, Col, Form} from 'react-bootstrap';
import { Card } from 'semantic-ui-react';

class PlaysPage extends Component {
  constructor(props) {
    super(props);

    this.state = {
        loading: false,
        play: {},
    };
  }

  componentDidMount() {
    this.setState({ loading: true });
    let playId = this.props.match.params.playId;
    console.log(playId);
    let show = this.props.firebase.db.collection("shows").doc(playId);
    this.unsubscribe = show.get().then(snapshot => {
        const plays = [];
        const { name, description, created } = snapshot.data();
        let play = {
            name,
            description,
            created,
            actors: [],
        };
        show.collection("actors").get().then(snapshot => {
          snapshot.forEach(doc => {
            play.actors.push(doc.data().name);
          });
        });
        console.log(play);

        this.setState({
            play,
            loading: false,
        });
    });
  }

  componentWillUnmount() {
      this.unsubscribe && this.unsubscribe();
  }


  render() {
    const { play, loading } = this.state;
    console.log(this.state);

    return (
      <Container>
        <Row>
          <Col>
            <h1>Conflict Form for {this.state.play.name}</h1>
            {loading && <div>Loading ...</div>}
            <Form>
              <Form.Group>
                <Form.Label>Who are you?</Form.Label>
                <Form.Control as="select">
                  {
                    this.state.play.actors.map((actor) =>
                      <option>actor</option>
                    )
                  }
                </Form.Control>
              </Form.Group>
            </Form>
        </Col>
      </Row>
      </Container>
    );
  }
}


const condition = authUser => !!authUser;

export default withAuthUser(withAuthorization(condition)(PlaysPage));
