import React, { Component } from 'react';
import { Button} from 'react-bootstrap';
import { Header, Form, Input } from 'semantic-ui-react';


class PlayActor extends Component {
  state = {
    actors: [{name:""}],
  }

  saveAndContinue = (e) => {
    e.preventDefault();
    this.props.nextStep();
  }

  back = (e) => {
    e.preventDefault();
    this.props.prevStep();
  }

  render() {
    const { values } = this.props;
    const isInvalid = 
        values.name === '';
    let actors = this.state;
    return (
     <Form >
      <Header as="h1">Actor</Header>
        { actors.map((val, indx) => {
          return (
            <Form.Group key={indx}>
              <Form.Field inline>
                <label>Name</label>
                <Input />
              </Form.Field>
            </Form.Group>
          )
          })
        }
        <Form.Group>
          <Button primary type="text">Add New Actor</Button>
        </Form.Group>
        <Button onClick={this.back} disabled={isInvalid} variant="light" type="submit">
            Back
        </Button>
        <Button onClick={this.saveAndContinue} disabled={isInvalid} variant="light" type="submit">
            Next
        </Button>
    </Form>
    );
  }
}

export default PlayActor;
