import React, { Component } from 'react';
import { Button, Col, Row, Container, Form } from 'react-bootstrap';
import InputGroup from 'react-bootstrap/InputGroup';
import TimePicker from '../../TimePicker';
import { Header } from 'semantic-ui-react';


class PlayActor extends Component {





  addUnavailibility = (i, e) => {
    let actors = [...this.state.actors];
    actors[i]["unavailibilities"] = [...this.state.actors[i]["unavailibilities"], {day: "sunday", start:32400, end:32400}];
    console.log(actors[i]);
    this.setState((prevState) => ({
      actors
    }));
  }



  removeUnavailbility = (i, dataIndx, e) => {
    let actors = [...this.state.actors];
    let data = actors[i]["unavailibilities"];
    let newData = data.slice(0, dataIndx).concat(data.slice(dataIndx+1, data.length));
    actors[i]["unavailibilities"] = newData;
    this.setState((prevState) => ({
      actors
    }));
  }



  saveAndContinue = (e) => {
    e.preventDefault();
    this.props.nextStep();
  }

  back = (e) => {
    e.preventDefault();
    this.props.prevStep();
  }

  render() {
    const { actors } = this.props.actors;
    return (
      <Form onChange={this.props.onChange}>
        <Header as="h1">Actors</Header>

          { actors.map((val, indx) => {
            return (
              <Container key={indx}>
                <Row>
                  <Col>
                      <Form.Group>
                        <Form.Label>Name</Form.Label>
                        <Form.Control type="text" data-type="outer" data-attr="name" data-actorid={indx} />
                      </Form.Group>
                      <Form.Group>
                        <Form.Label>Characters</Form.Label>
                          { actors[indx]["characters"].map((val, charIndx) => {
                              const last = charIndx == actors[indx]["characters"].length - 1;
                              return (
                                <div key={charIndx}>
                                  {last ? (
                                    <InputGroup className="mb-3">
                                      <InputGroup.Prepend>
                                        <Button variant="danger" onClick={this.removeCharacter.bind(this, indx, charIndx)}>-</Button>
                                      </InputGroup.Prepend>
                                      <Form.Control
                                        type="text"
                                        data-type="inner"
                                        data-attr="characters"
                                        data-actorid={indx}
                                        data-attrid={charIndx}
                                        aria-describedby="basic-addon2"
                                        value={val}
                                        onChange={()=>{}}
                                      />
                                      <InputGroup.Append>
                                        <Button variant="success" onClick={this.addCharacter.bind(this, indx)}>Add Character</Button>
                                      </InputGroup.Append>
                                    </InputGroup>
                                  ) : (
                                    <InputGroup className="mb-3">
                                      <InputGroup.Prepend>
                                        <Button variant="danger" onClick={this.removeCharacter.bind(this, indx, charIndx)}>-</Button>
                                      </InputGroup.Prepend>
                                      <Form.Control
                                        type="text"
                                        data-type="inner"
                                        data-attr="characters"
                                        data-actorid={indx}
                                        data-attrid={charIndx}
                                        aria-describedby="basic-addon2"
                                        value={val}
                                        onChange={()=>{}}
                                      />
                                    </InputGroup>
                                  )}
                                </div>
                              );
                            })
                          }
                      </Form.Group>
                      <Form.Group controlId="exampleForm.ControlSelect1">
                        <Form.Label>Unavailibilities</Form.Label>
                        { actors[indx]["unavailibilities"].map((data, dataIndx) => {
                          let day = data["day"];
                          let start = data["start"];
                          let end = data["end"];
                          return (
                            <Form.Row key={dataIndx}>
                              <Form.Group as={Col} xs={1}>
                                <Button variant="danger" onClick={this.removeUnavailbility.bind(this, indx, dataIndx)}>-</Button>
                              </Form.Group>
                              <Form.Group as={Col}>
                                <Form.Control
                                  as="select"
                                  data-type="inner"
                                  data-attr="unavailibilities"
                                  data-actorid={indx}
                                  data-attrid={dataIndx}
                                  data-timeattr="day"
                                  value={day}
                                  onChange={()=>{}}
                                  >
                                  <option value="sunday">Sunday</option>
                                  <option value="monday">Monday</option>
                                  <option value="tuesday">Tuesday</option>
                                  <option value="wednesday">Wednesday</option>
                                  <option value="thursday">Thursday</option>
                                  <option value="friday">Friday</option>
                                  <option value="saturday">Saturday</option>
                                </Form.Control>
                              </Form.Group>
                              <Form.Group as={Col}>
                                  <TimePicker
                                    start="9:00"
                                    end="22:00"
                                    step={15}
                                    data-type="inner"
                                    data-attr="unavailibilities"
                                    data-actorid={indx}
                                    data-attrid={dataIndx}
                                    data-timeattr="start"
                                    value={start}
                                    onChange={()=>{}}
                                   />
                              </Form.Group>
                              <Form.Group as={Col}>
                                <TimePicker
                                    start="9:00"
                                    end="22:00"
                                    step={15}
                                    isInvalid={end <= start}
                                    data-type="inner"
                                    data-attr="unavailibilities"
                                    data-actorid={indx}
                                    data-attrid={dataIndx}
                                    data-timeattr="end"
                                    value={end}
                                    onChange={()=>{}}
                                   />
                              </Form.Group>
                            </Form.Row>
                          );
                        })
                      }
                      <Form.Row>
                        <Form.Group as={Col}>
                          <Button variant="success" onClick={this.addUnavailibility.bind(this, indx)}>Add</Button>
                        </Form.Group>
                      </Form.Row>
                      </Form.Group>
                  </Col>
                  <Col>
                    <Form.Group>
                      <Form.Label>Notes</Form.Label>
                      <Form.Control as="textarea" data-type="outer" data-attr="notes" data-actorid={indx} rows={9}/>
                    </Form.Group>
                  </Col>
                </Row>
                <hr />
              </Container>
            )
            })
          }
          <Form.Group>
            <Button type="text" onClick={this.addActor}>Add New Actor</Button>
          </Form.Group>
          <Button onClick={this.back} disabled={isInvalid} variant="light" type="submit">
              Back
          </Button>
          <Button onClick={this.saveAndContinue} disabled={isInvalid} variant="light" type="submit">
              Next
          </Button>
      </Form>
    );
  }
}

export default PlayActor;
