import React, { Component } from 'react';
import { Button, Col, Row, Container, Form } from 'react-bootstrap';
import InputGroup from 'react-bootstrap/InputGroup';
import TimePicker from '../../TimePicker';
import { Header } from 'semantic-ui-react';


class PlayActor extends Component {
  saveAndContinue = (e) => {
    e.preventDefault();
    this.props.nextStep();
  }

  back = (e) => {
    e.preventDefault();
    this.props.prevStep();
  }

  render() {
    const { values } = this.props;
    let isInvalid = false;
    let scenes = values.scenes;

    let characters = new Set();
    values.actors.forEach((actor) => {
      actor.characters.forEach((character) => {
        characters.add(character);
      })
    });
    characters = Array.from(characters);
    console.log(characters);
    return (
      <Form onChange={this.props.onChange}>
        <Header as="h1">Scenes</Header>

          { scenes.map((val, indx) => {
            return (
              <Container key={indx}>
                <Row>
                  <Col>
                      <Form.Group>
                        <Form.Label>Title</Form.Label>
                        <Form.Control type="text" data-collection="scenes" data-attr="title" data-elementid={indx} placeholder="E.g. 1.1a"/>
                      </Form.Group>
                      <Form.Group>
                        <Form.Label>Characters</Form.Label>
                          { scenes[indx]["characters"].map((val, charIndx) => {
                              const last = charIndx == scenes[indx]["characters"].length - 1;
                              return (
                                <div key={charIndx}>
                                  <InputGroup className="mb-3">
                                    <InputGroup.Prepend>
                                      <Button variant="danger" onClick={this.props.removeCharacter.bind(this, "scenes", indx, charIndx)}>-</Button>
                                    </InputGroup.Prepend>
                                    <Form.Control
                                      as="select"
                                      data-collection="scenes"
                                      data-elementid={indx}
                                      data-charrid={charIndx}
                                      value={val}
                                      onChange={this.props.updateCharacter}
                                      >
                                        <option value="">--</option>
                                      { characters.map((val, index) => {
                                        return (
                                          <option key={index} value={val}>{ val }</option>
                                        );
                                      })}
                                    </Form.Control>
                                  </InputGroup>
                                </div>
                              );
                            })
                          }
                      </Form.Group>
                      <Form.Group>
                        <Button variant="success" onClick={this.props.addCharacter.bind(this, "scenes", indx)}>Add Character</Button>
                      </Form.Group>
                  </Col>
                  <Col>
                    <Form.Group>
                      <Form.Label>Notes</Form.Label>
                      <Form.Control as="textarea" data-collection="scenes" data-attr="notes" data-elementid={indx} rows={9}/>
                    </Form.Group>
                  </Col>
                </Row>
                <hr />
              </Container>
            )
            })
          }
          <Container>
            <Row>
              <Col>
              <Form.Group>
                <Button type="text" onClick={this.props.addScene}>Add New Scene</Button>
              </Form.Group>
            </Col>
            <Col className="text-right">
              <Form.Group>
                <Button onClick={this.back} disabled={isInvalid} variant="light" type="submit">
                  Back
                </Button>
                <Button onClick={this.saveAndContinue} disabled={isInvalid} variant="light" type="submit">
                    Next
                </Button>
              </Form.Group>
              </Col>
            </Row>
          </Container>

      </Form>
    );
  }
}

export default PlayActor;
