import React, { Component } from 'react';
import { Button} from 'react-bootstrap';
import { Header, Form, Input, List } from 'semantic-ui-react';


class PlayActor extends Component {
  state = {
    actors: [{name:""},],
  }

  addActor = (e) => {
    this.setState((prevState) => ({
      actors: [...prevState.actors, {name:""}],
    }));
  }

  saveAndContinue = (e) => {
    e.preventDefault();
    this.props.nextStep();
  }

  back = (e) => {
    e.preventDefault();
    this.props.prevStep();
  }

  render() {
    const { values } = this.props;
    const isInvalid = 
        values.name === '';
    let {actors} = this.state;
    return (
     <Form >
      <Header as="h1">Actor</Header>
        <List>

        { actors.map((val, indx) => {
          return (
          <List.Item>
            <Form key={indx}>
              <Form.Field >
                <label>Name</label>
                <Input />
              </Form.Field>
            </Form>
              <List.List>
                <List.Item>
                  <List.Icon/>
                  <List.Content>
                    <Form>
                      <Form.Field >
                      <label>Characters</label>
                      <Input />
                      </Form.Field>
                    </Form>
                  </List.Content>
                </List.Item>
              </List.List>
          </List.Item>
          )
          })
        }
        </List>
        <Form.Group>
          <Button primary={true} type="text" onClick={this.addActor}>Add New Actor</Button>
        </Form.Group>
        <Button onClick={this.back} disabled={isInvalid} variant="light" type="submit">
            Back
        </Button>
        <Button onClick={this.saveAndContinue} disabled={isInvalid} variant="light" type="submit">
            Next
        </Button>
    </Form>
    );
  }
}

export default PlayActor;
