import React, { Component } from 'react';
import { Button, Col, Row, Container} from 'react-bootstrap';
import { Header, Form, Input, TextArea, List } from 'semantic-ui-react';


class PlayActor extends Component {
  state = {
    actors: [{name:"", characters:[],},],
  }

  addActor = (e) => {
    this.setState((prevState) => ({
      actors: [...prevState.actors, {name:""}],
    }));
  }

  saveAndContinue = (e) => {
    e.preventDefault();
    this.props.nextStep();
  }

  back = (e) => {
    e.preventDefault();
    this.props.prevStep();
  }

  render() {
    const { values } = this.props;
    const isInvalid = 
        values.name === '';
    let {actors} = this.state;
    return (
    <Row>
      <Col>
        <Form >
          <Header as="h1">Actor</Header>

            { actors.map((val, indx) => {
              return (
                <Container>
                  <Row>
                    <Col>
                        <Form.Field>
                          <label>Name</label>
                          <Input />
                        </Form.Field>
                    </Col>
                  </Row>
                  <Row>
                    <Col xs={{ span: 11, offset: 1}}>
                        <Form.Field>
                          <label>Characters</label>
                          <Input />
                        </Form.Field>
                        <Button primary={true} type="text" onClick={this.addCharacter}>Add Character</Button>
                    </Col>
                  </Row>
                  <Row>
                    <Col xs={{ span: 11, offset: 1}}>
                        <Form.Field>
                          <label>Unavailibility</label>
                          <Input />
                        </Form.Field>
                    </Col>
                  </Row>
                  <Row>
                    <Col xs={{ span: 11, offset: 1}}>
                        <Form.Field>
                          <label>Notes</label>
                          <TextArea />
                        </Form.Field>
                    </Col>
                  </Row>
                  <hr />
                </Container>
              )
              })
            }



            <Form.Group>
              <Button primary={true} type="text" onClick={this.addActor}>Add New Actor</Button>
            </Form.Group>
            <Button onClick={this.back} disabled={isInvalid} variant="light" type="submit">
                Back
            </Button>
            <Button onClick={this.saveAndContinue} disabled={isInvalid} variant="light" type="submit">
                Next
            </Button>
        </Form>
      </Col>
    </Row>
    );
  }
}

export default PlayActor;
