import React, { Component } from 'react';
import { Button, Col, Row, Container, Form } from 'react-bootstrap';
import InputGroup from 'react-bootstrap/InputGroup';
import TimePicker from '../../TimePicker';
import { Header } from 'semantic-ui-react';


class Review extends Component {
  saveAndContinue = (e) => {
    e.preventDefault();
    this.props.nextStep();
  }

  back = (e) => {
    e.preventDefault();
    this.props.prevStep();
  }

  render() {
    const { values } = this.props;
    let scenes = values.scenes;
    let actors = values.actors;
    let name = values.name;
    let description = values.description;

    return (
      <div>
        <Header as="h1">Review and Submit</Header>
        <Header as="h3">Name: </Header><p>{name}</p>
        <Header as="h3">Description: </Header><p>{description}</p>
        <hr />
        <Container>
          <Row>
            <Col></Col>
            <Col className="text-right">
              <Form>
                <Form.Group>
                  <Button onClick={this.back}  variant="light" type="submit">
                    Back
                  </Button>
                  <Button onClick={this.saveAndContinue} variant="light" type="submit">
                      Next
                  </Button>
                </Form.Group>
              </Form>
            </Col>
          </Row>
        </Container>
      </div>
    );
  }
}

export default Review;
