import React, { Component } from 'react';

import { withRouter, Link } from 'react-router-dom';
import { withAuthUser, withAuthorization, withAuthentication } from '../Session';
import { withFirebase } from '../Firebase';
import { Container, Row, Col } from 'react-bootstrap';
import { Tab } from 'semantic-ui-react';

class PlayPage extends Component {
  constructor(props) {
    super(props);

    this.state = {
        loading: false,
        plays: [],
    };
  }

  componentDidMount() {
    this.setState({ loading: true });

    this.unsubscribe = this.props.firebase.db.collection("shows").where("creator", "==", this.props.context.uid).onSnapshot(snapshot => {
        const plays = [];
        snapshot.forEach((doc) => {
            const { name, description, created } = doc.data();
            plays.push({
                key: doc.id,
                doc,
                name,
                description,
                created,
            });
        });
        this.setState({
            plays,
            loading: false,
        });
    });
  }

  componentWillUnmount() {
      this.unsubscribe && this.unsubscribe();
  }


  render() {
    const { plays, loading } = this.state;
    console.log('here');

    var panes = [
      { menuItem: 'Shedule', render: () => <Tab.Pane attached={false}>Tab 1 Content</Tab.Pane>},
      { menuItem: 'Tab 2', render: () => <Tab.Pane attached={false}>Tab 2 Content</Tab.Pane> },
      { menuItem: 'Tab 3', render: () => <Tab.Pane attached={false}>Tab 3 Content</Tab.Pane> },
    ];

    return (
        <Row>
          <Col>
            <h1>Play</h1>
            <Tab menu={{ secondary: true, pointing: true}} panes={panes} />
        </Col>
      </Row>
    );
  }
}


const condition = authUser => !!authUser;

export default withRouter(withAuthUser(withAuthorization(condition)(PlayPage)));
