import React, { Component } from 'react';
import { Container, Row, Col, Form, Button} from 'react-bootstrap';


class PlayName extends Component {
  saveAndContinue = (e) => {
    e.preventDefault();
    this.props.nextStep();
  }

  render() {
    const isInvalid = 
        name === '';
    const { values } = this.props;
    return (
     <Form
      >
        <Form.Group controlId="playName">
            <Form.Label>Name</Form.Label>
            <Form.Control name="name" onChange={this.onChange} type="text" defaultValue={values.name}></Form.Control>
        </Form.Group>
        <Button onClick={this.saveAndContinue} disabled={isInvalid} variant="light" type="submit">
            Next
        </Button>
    </Form>
    );
  }
}

export default PlayName;
