import React, { Component } from 'react';
import { Button, Col, Row, Container} from 'react-bootstrap';
import { Header, Form, Input, TextArea, List } from 'semantic-ui-react';


class PlayActor extends Component {
  state = {
    actors: [{name:"", characters:[""],},],
  }

  addActor = (e) => {
    this.setState((prevState) => ({
      actors: [...prevState.actors, {name:""}],
    }));
  }

  addCharacter = (i, e) => {
    let actors = [...this.state.actors];
    console.log(actors[i]);
    actors[i]["characters"] = [...this.state.actors[i]["characters"], ""];
    console.log(actors[i]);
    this.setState((prevState) => ({
      actors
    }));
  }

  saveAndContinue = (e) => {
    e.preventDefault();
    this.props.nextStep();
  }

  back = (e) => {
    e.preventDefault();
    this.props.prevStep();
  }

  render() {
    const { values } = this.props;
    const isInvalid =
        values.name === '';
    let {actors} = this.state;
    return (
    <Row>
      <Col>
        <Form >
          <Header as="h1">Actor</Header>

            { actors.map((val, indx) => {
              return (
                <Container key={indx}>
                  <Row>
                    <Col>
                        <Form.Field>
                          <label>Name</label>
                          <Input />
                        </Form.Field>
                    </Col>
                  </Row>
                    <Row>
                    <Col xs={{ span: 10, offset: 0}}>
                        <Form.Field>
                          <label>Characters</label>
                            { actors[indx]["characters"].map((val, charIndx) => {
                                return (
                                  <Input key={charIndx} type="text" data-id={indx} id={charIndx} value={actors[indx]["characters"][charIndx]}/>
                                )
                              })
                            }
                        </Form.Field>
                        <Button variant="primary" onClick={this.addCharacter.bind(this, indx)}>Add Character</Button>
                    </Col>
                  </Row>
                  <Row>
                    <Col xs={{ span: 11, offset: 1}}>
                        <Form.Field>
                          <label>Unavailibility</label>
                          <Input />
                        </Form.Field>
                    </Col>
                  </Row>
                  <Row>
                    <Col xs={{ span: 11, offset: 1}}>
                        <Form.Field>
                          <label>Notes</label>
                          <TextArea />
                        </Form.Field>
                    </Col>
                  </Row>
                  <hr />
                </Container>
              )
              })
            }



            <Form.Group>
              <Button type="text" onClick={this.addActor}>Add New Actor</Button>
            </Form.Group>
            <Button onClick={this.back} disabled={isInvalid} variant="light" type="submit">
                Back
            </Button>
            <Button onClick={this.saveAndContinue} disabled={isInvalid} variant="light" type="submit">
                Next
            </Button>
        </Form>
      </Col>
    </Row>
    );
  }
}

export default PlayActor;
