import React, { Component } from 'react';
import { Button} from 'react-bootstrap';
import { Header, Form, Input } from 'semantic-ui-react';


class PlayActor extends Component {
  saveAndContinue = (e) => {
    e.preventDefault();
    this.props.nextStep();
  }

  back = (e) => {
    e.preventDefault();
    this.props.prevStep();
  }

  render() {
    const { values } = this.props;
    const isInvalid = 
        values.name === '';
    return (
     <Form >
      <Header as="h1">Actor</Header>
        <Form.Group controlId="playActor">
          <Form.Field inline>
            <label>Name</label>
            <Input />
          </Form.Field>
        </Form.Group>
        <Form.Group>
          <button type="text">Add New Actor</button>
        </Form.Group>
        <Button onClick={this.back} disabled={isInvalid} variant="light" type="submit">
            Back
        </Button>
        <Button onClick={this.saveAndContinue} disabled={isInvalid} variant="light" type="submit">
            Next
        </Button>
    </Form>
    );
  }
}

export default PlayActor;
