import React, { Component } from 'react';

import { Link } from 'react-router-dom';
import { withAuthUser, withAuthorization, withAuthentication } from '../Session';
import { withFirebase } from '../Firebase';
import { Container, Row, Col } from 'react-bootstrap';
import { Card } from 'semantic-ui-react';

class PlaysPage extends Component {
  constructor(props) {
    super(props);

    this.state = {
        loading: false,
        play: {},
    };
  }

  componentDidMount() {
    this.setState({ loading: true });
    let playId = this.props.match.params.playId;
    console.log(playId);

    this.unsubscribe = this.props.firebase.db.collection("shows").doc(playId).get().then(snapshot => {
        const plays = [];
        const { name, description, created, characters } = snapshot.data();
        let play = {
            name,
            description,
            created,
            characters,
        };
        console.log(play);
        this.setState({
            play,
            loading: false,
        });
    });
  }

  componentWillUnmount() {
      this.unsubscribe && this.unsubscribe();
  }


  render() {
    const { plays, loading } = this.state;

    return (
        <Row>
          <Col>
            <h1>Add Conflict</h1>
            {loading && <div>Loading ...</div>}
        </Col>
      </Row>
    );
  }
}


const condition = authUser => !!authUser;

export default withAuthUser(withAuthorization(condition)(PlaysPage));
